import React from 'react';
import ChatBot from 'react-simple-chatbot';
import Review from './Review';

const SimpleForm = () => {
  fetch("http://localhost:9292/steps")
  .then((r) => r.json())
  .then((data) => console.log(data));
       return (
        <ChatBot

          steps={[
            {
              id: '1',
              message: 'Hello, enter any Ruby syntax',
              trigger: 'name',
            },
            {
              id: 'name',
              user: true,
              trigger: '3',
            },
            {
              id: '3',
              message: 'Ruby is an interpreted, high-level, general-purpose programming language which supports multiple programming paradigms',
              trigger: 'gender',
            },
            {
              id: 'gender',
              message: '?',
              trigger: '5',
            },
           {
              id: '5',
              message: 'How old ar',
              trigger: 'age',
            },
            {
              id: 'age',
              message: 'How old are?',
              trigger: '7',
            },
            {
              id: '7',
              message: 'Great! Check out your summary',
              trigger: 'review',
            },
            {
              id: 'review',
              component: <Review />,
              asMessage: true,
              trigger: 'update',
            },
          ]}
        />
      );
    }


  export default SimpleForm;
